// Frontend: CheckoutButton.js
import React from "react";
import { loadStripe } from "@stripe/stripe-js";
import axios from "axios";

const stripePromise = loadStripe("your_stripe_public_key"); // Use your actual Stripe public key

const CheckoutButton = ({ cartItems }) => {
  const handleCheckout = async () => {
    try {
      const stripe = await stripePromise;

      const { data } = await axios.post("http://localhost:5000/api/checkout/create-checkout-session", {
        cartItems,
      });

      const result = await stripe.redirectToCheckout({
        sessionId: data.id,
      });

      if (result.error) {
        console.error("Stripe error:", result.error.message);
      }
    } catch (error) {
      console.error("Checkout failed:", error);
    }
  };

  return (
    <button
      onClick={handleCheckout}
      className="btn btn-success fw-bold px-4 py-2 mt-3"
    >
      ðŸ›’ Checkout
    </button>
  );
};

export default CheckoutButton;
